import React, { useState } from "react";
import FormInput from "./form-input";

export default function Form(props) {
  const [tag, setTag] = useState(genres);
  console.log(genres);
  const { handlerChange, state } = props;
  const {
    title,
    description,
    year,
    release_date,
    genres,
    mpaa_rating,
    rating,
    runtime,
    errors,
  } = state;

  return (
    <>
      <FormInput
        label="title"
        name="title"
        value={title}
        handlerChange={handlerChange}
        error={errors && errors.includes("title")}
      />
      <FormInput
        label="year"
        name="year"
        value={year}
        handlerChange={handlerChange}
        error={errors && errors.includes("year")}
      />
      <FormInput
        label="runtime"
        name="runtime"
        value={runtime}
        handlerChange={handlerChange}
        error={errors && errors.includes("runtime")}
      />
      <FormInput
        label="rating"
        name="rating"
        value={rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("rating")}
      />
      <FormInput
        label="mpaa rating"
        name="mpaa_rating"
        value={mpaa_rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("mpaa_rating")}
      />
      <FormInput
        label="release date"
        name="release_date"
        value={release_date}
        handlerChange={handlerChange}
      />

      {/* <FormInput
        label="genres"
        name="genres"
        value={genres}
        handlerChange={handlerChange}
        error={errors && errors.includes("genres")}
      /> */}

      <div className="level">
        <div className="level-left">
          <div className="field">
            <label className="label">Subject</label>
            <div className="control">
              <div className="select">
                <select>
                  <option>Select dropdown</option>
                  <option>With options</option>
                </select>
              </div>
            </div>
          </div>
        </div>
        <div className="level-right">
          <div className="tags">
            <span className="tag is-success">
              Bar
              <button className="delete is-small"></button>
            </span>
          </div>
        </div>
      </div>

      <FormInput
        label="description"
        name="description"
        value={description}
        handlerChange={handlerChange}
        error={errors && errors.includes("description")}
      />
    </>
  );
}
