import React from "react";

export default function FormInput(props) {
  const { label, name, value, handlerChange, text = "text" } = props;
  return (
    <div className="field">
      <label className="label is-capitalized">
        {label.replaceAll("_", " ")}
      </label>
      <div className="control">
        {text === "text" ? (
          <input
            className="input"
            type="text"
            name={name}
            value={value}
            onChange={handlerChange}
          />
        ) : (
          <textarea
            className="textarea"
            name={name}
            value={value}
            onChange={handlerChange}
          ></textarea>
        )}
      </div>
    </div>
  );
}
