import { useState } from "react";
import Form from "../components/form";
const initValues = {
  title: "",
  release_date: "",
  runtime: "",
  mpaa_rating: "",
  rating: "",
  description: "",
};
export default function AddEditMovie() {
  const [state, setState] = useState(initValues);
  const handlerChange = (e) => {
    const { name, value } = e.target;
    setState((prev) => ({ ...prev, [name]: value }));
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(state);
  };
  return (
    <section className="hero">
      <div className="hero-body">
        <div className="level">
          <p className="title level-left">Add/Edit Movie</p>
          <div className="select level-right">
            <select onChange={(e) => console.log(e.target)} name="mpaa_rating">
              <option>Choose...</option>
              <option value="g">G</option>
              <option value="pg">PG</option>
              <option value="pg13">PG13</option>
              <option value="R">R</option>
              <option value="nc17">NC17</option>
            </select>
          </div>
        </div>

        <div className="content">
          <form onSubmit={handleSubmit}>
            <Form handlerChange={handlerChange} state={state} />
            <div className="field is-grouped is-grouped-right">
              <p className="control">
                <button className="button is-primary" type="submit">
                  Save
                </button>
              </p>
              <p className="control">
                <button className="button is-light">Cancel</button>
              </p>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
}
