import { useState, useEffect } from "react";

const fetchMovies = async () => {
  try {
    const res = await fetch(`http://localhost:4000/v1/genres`);
    if (!res.ok) {
      console.log(res);
      throw new Error(res.statusText);
    }
    return res.json();
  } catch (error) {
    return error.message;
  }
};
export default function LazyGenres({ genres }) {
  const [state, setState] = useState({
    genres: genres || [],
    error: null,
    loading: true,
    tagGenre: [],
  });
  useEffect(() => {
    const fetchData = async () => {
      const res = await fetchMovies();
      if (!res.genres) {
        setState((prev) => ({ ...prev, loading: false, error: res }));
      } else {
        setState((prev) => ({ ...prev, loading: false, genres: res.genres }));
      }
    };
    fetchData();
  }, []);

  const handleTag = (e) => {
    let newArray = state.tagGenre;
    newArray.push({ [e.target.value]: e.target.name });
    setState((prev) => ({ ...prev, tagGenre: newArray }));
  };

  return (
    <div className="level">
      <div className="level-left">
        <div className="field">
          <label className="label">Genres</label>
          <div className="control">
            <div className="select">
              <select>
                <option value={0}>Choose...</option>
                {state.genres.map((g) => (
                  <option key={g.id} value={g.id} onClick={handleTag}>
                    {g.genre_name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>
      <div className="level-right">
        <div className="tags">
          {state.tagGenre.map((el) => console.log(el))}
        </div>
      </div>
    </div>
  );
}
