import React from "react";

export default function FormInput(props) {
  const { label, name, value, handlerChange } = props;

  const inputField = (key) => {
    console.log(ky);
    switch (key) {
      case key === "description":
        return (
          <textarea
            className="textarea"
            name={name}
            value={value}
            onChange={handlerChange}
          ></textarea>
        );
      case key === "mpaa_rating":
        return (
          <div className="select">
            <select>
              <option>Select dropdown</option>
              <option>With options</option>
            </select>
          </div>
        );

      default:
        return (
          <input
            className="input"
            type="text"
            name={name}
            value={value}
            onChange={handlerChange}
          />
        );
    }
  };
  return (
    <div className="field">
      <label className="label is-capitalized">
        {label.replaceAll("_", " ")}
      </label>
      <div className="control">{inputField(name)}</div>
    </div>
  );
}
