import { useEffect, useState } from "react";
import { Link, Outlet } from "react-router-dom";

const fetchMovies = async () => {
  const res = await fetch("http://localhost:4000/v1/movies");
  return res.json();
};

export default function Movies() {
  const [state, setState] = useState({ loading: true, movies: [] });
  useEffect(() => {
    const fetchData = async () => {
      const res = await fetchMovies();
      setState((prev) => ({ ...prev, movies: res.movies }));
    };
    fetchData();
  }, []);
  if (state.loading) {
    return <p>loading...</p>;
  }
  return (
    <section className="hero">
      <div className="hero-body">
        <p className="title">Choose a Movie</p>
        <div className="content">
          <ul>
            <li>
              <Link to="/movies/1">The Shawshank Redemption</Link>
            </li>
            <li>
              <Link to="/movies/2">The Godfather</Link>
            </li>
            <li>
              <Link to="/movies/3">The Dark Night</Link>
            </li>
          </ul>
        </div>
        <Outlet />
      </div>
    </section>
  );
}
