import React from "react";

export default function SearchBox({ state, setState }) {
  const handleChange = (e) => {
    const { value } = e.target;
    setState((prev) => ({ ...prev, ["searchTerm"]: value }));
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const query = `
    search(titleContains: "${state.searchTerm}"){
      id
      title
      year
      description
      runtime
    }
    `;
    fetch("http://localhost:4000/v1/graphql", {
      signal: signal,
      method: "post",
      body: query,
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then(({ data }) => {
        console.log(data);
        setState({ movies: [], loading: false });
      })
      .catch((err) => {
        if (err.name === "AbortError") {
          console.log("successfully aborted");
        } else {
          console.log(err);
          // setError(err);
        }
      });
  };
  return (
    <div className="field has-addons">
      <div className="control is-expanded">
        <input
          className="input"
          type="text"
          placeholder="Find a movie title"
          // value={}
          // name="searchTerm"
          onChange={handleChange}
        />
      </div>
      <div className="control">
        <button className="button is-info" onClick={handleSubmit} type="button">
          Search
        </button>
      </div>
    </div>
  );
}
