import React from "react";
import FormInput from "./form-input";

export default function Form(props) {
  const { handlerChange, state } = props;
  const {
    title,
    description,
    year,
    release_date,
    genres,
    mpaa_rating,
    rating,
    runtime,
    errors,
  } = state;

  return (
    <>
      <FormInput
        label="title"
        name="title"
        value={title}
        handlerChange={handlerChange}
        error={errors && errors.includes("title")}
      />
      <FormInput
        label="year"
        name="year"
        value={year}
        handlerChange={handlerChange}
        error={errors && errors.includes("year")}
      />
      <FormInput
        label="runtime"
        name="runtime"
        value={runtime}
        handlerChange={handlerChange}
        error={errors && errors.includes("runtime")}
      />
      <FormInput
        label="rating"
        name="rating"
        value={rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("rating")}
      />
      <FormInput
        label="mpaa rating"
        name="mpaa_rating"
        value={mpaa_rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("mpaa_rating")}
      />
      <FormInput
        label="release date"
        name="release_date"
        value={release_date}
        handlerChange={handlerChange}
      />

      {/* <FormInput
        label="genres"
        name="genres"
        value={genres}
        handlerChange={handlerChange}
        error={errors && errors.includes("genres")}
      /> */}
      <div className="select is-multiple">
        <select multiple size="5">
          <option value="Argentina">Argentina</option>
          <option value="Bolivia">Bolivia</option>
          <option value="Brazil">Brazil</option>
          <option value="Chile">Chile</option>
          <option value="Colombia">Colombia</option>
          <option value="Ecuador">Ecuador</option>
          <option value="Guyana">Guyana</option>
          <option value="Paraguay">Paraguay</option>
          <option value="Peru">Peru</option>
          <option value="Suriname">Suriname</option>
          <option value="Uruguay">Uruguay</option>
          <option value="Venezuela">Venezuela</option>
        </select>
      </div>
      <FormInput
        label="description"
        name="description"
        value={description}
        handlerChange={handlerChange}
        error={errors && errors.includes("description")}
      />
    </>
  );
}
