import React from "react";

export default function FormInput(props) {
  const { label, name, value, handlerChange } = props;
  return (
    <div className="field">
      <label className="label is-capitalized">
        {label.replaceAll("_", " ")}
      </label>
      <div className="control">
        <input
          className="input"
          type="text"
          name={name}
          value={value}
          onChange={handlerChange}
        />
      </div>
    </div>
  );
}
