import FormInput from "./form-input";
import BulmaTagsInput from "@creativebulma/bulma-tagsinput";
// const BulmaTagsInput = React.lazy(() =>
//   import("@creativebulma/bulma-tagsinput")
// );
console.log(BulmaTagsInput);
export default function Form(props) {
  const { handlerChange, state } = props;
  const {
    title,
    description,
    year,
    release_date,
    genres,
    mpaa_rating,
    rating,
    runtime,
    errors,
  } = state;

  return (
    <>
      <FormInput
        label="title"
        name="title"
        value={title}
        handlerChange={handlerChange}
        error={errors && errors.includes("title")}
      />
      <FormInput
        label="year"
        name="year"
        value={year}
        handlerChange={handlerChange}
        error={errors && errors.includes("year")}
      />
      <FormInput
        label="runtime"
        name="runtime"
        value={runtime}
        handlerChange={handlerChange}
        error={errors && errors.includes("runtime")}
      />
      <FormInput
        label="rating"
        name="rating"
        value={rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("rating")}
      />
      <FormInput
        label="mpaa rating"
        name="mpaa_rating"
        value={mpaa_rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("mpaa_rating")}
      />
      <FormInput
        label="release date"
        name="release_date"
        value={release_date}
        handlerChange={handlerChange}
      />
      {/* <FormInput
        label="genres"
        name="genres"
        value={genres}
        handlerChange={handlerChange}
        error={errors && errors.includes("genres")}
      /> */}
      &lt;<span style="color:#309;font-weight:bold">div</span>{" "}
      <span style="color:#309">class</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"field"</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">label</span>{" "}
      <span style="color:#309">class</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"label"</span>&gt;Tags&lt;/
      <span style="color:#309;font-weight:bold">label</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">div</span>{" "}
      <span style="color:#309">class</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"control"</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">select</span>{" "}
      <span style="color:#309">multiple</span>{" "}
      <span style="color:#309">data-type</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"tags"</span>{" "}
      <span style="color:#309">data-placeholder</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"Choose Tags"</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">option</span>{" "}
      <span style="color:#309">value</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"one"</span>{" "}
      <span style="color:#309">selected</span>&gt;One&lt;/
      <span style="color:#309;font-weight:bold">option</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">option</span>{" "}
      <span style="color:#309">value</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"two"</span>{" "}
      <span style="color:#309">selected</span>&gt;Two&lt;/
      <span style="color:#309;font-weight:bold">option</span>&gt; &lt;
      <span style="color:#309;font-weight:bold">option</span>{" "}
      <span style="color:#309">value</span>
      <span style="color:#555">=</span>
      <span style="color:#c30">"three"</span>&gt;Three&lt;/
      <span style="color:#309;font-weight:bold">option</span>&gt; &lt;/
      <span style="color:#309;font-weight:bold">select</span>&gt; &lt;/
      <span style="color:#309;font-weight:bold">div</span>&gt; &lt;/
      <span style="color:#309;font-weight:bold">div</span>&gt;
      <FormInput
        label="description"
        name="description"
        value={description}
        handlerChange={handlerChange}
        error={errors && errors.includes("description")}
      />
    </>
  );
}
