import FormInput from "./form-input";

export default function Form(props) {
  const { handlerChange, state } = props;
  const {
    title,
    description,
    year,
    release_date,
    genres,
    mpaa_rating,
    rating,
    runtime,
    errors,
  } = state;

  return (
    <>
      <FormInput
        label="title"
        name="title"
        value={title}
        handlerChange={handlerChange}
        error={errors && errors.includes("title")}
      />
      <FormInput
        label="year"
        name="year"
        value={year}
        handlerChange={handlerChange}
        error={errors && errors.includes("year")}
      />
      <FormInput
        label="runtime"
        name="runtime"
        value={runtime}
        handlerChange={handlerChange}
        error={errors && errors.includes("runtime")}
      />
      <FormInput
        label="rating"
        name="rating"
        value={rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("rating")}
      />
      <FormInput
        label="mpaa rating"
        name="mpaa_rating"
        value={mpaa_rating}
        handlerChange={handlerChange}
        error={errors && errors.includes("mpaa_rating")}
      />
      <FormInput
        label="release date"
        name="release_date"
        value={release_date}
        handlerChange={handlerChange}
      />

      {/* <FormInput
        label="genres"
        name="genres"
        value={genres}
        handlerChange={handlerChange}
        error={errors && errors.includes("genres")}
      /> */}
      <div className="field">
        <label className="label">Tags</label>
        <div className="control content">
          <select multiple data-type="tags" data-placeholder="Choose Tags">
            <option value="one" selected>
              One
            </option>
            <option value="two" selected>
              Two
            </option>
            <option value="three">Three</option>
          </select>
        </div>
      </div>

      <FormInput
        label="description"
        name="description"
        value={description}
        handlerChange={handlerChange}
        error={errors && errors.includes("description")}
      />
    </>
  );
}
