import { useEffect, useState } from "react";
import { Link, Outlet } from "react-router-dom";

const fetchMovies = async () => {
  try {
    const res = await fetch("http://localhost:4000/v1/moviess");
    if (!res.ok) {
      throw new Error(res.status);
    }
    return res.json();
  } catch (error) {
    return error;
  }
};

export default function Movies() {
  const [state, setState] = useState({ loading: true, movies: [] });
  useEffect(() => {
    const fetchData = async () => {
      const res = await fetchMovies();
      console.log(Object.keys(res));
      setState((prev) => ({ ...prev, loading: false, movies: res.movies }));
    };
    fetchData();
  }, []);
  if (state.loading) {
    return <p>loading...</p>;
  }

  return (
    <section className="hero">
      <div className="hero-body">
        <p className="title">Choose a Movie</p>
        <div className="content">
          <ul>
            {state.movies.map((mv) => (
              <li key={mv.id}>
                <Link to={`/movies/${mv.id}`}>{mv.title}</Link>
              </li>
            ))}
          </ul>
        </div>
        <Outlet />
      </div>
    </section>
  );
}
