import { useState, useContext, createContext } from "react";

const JwtStateContext = createContext();
const SetJwtStateContext = createContext();

export const JwtProvider = ({ children }) => {
  const [jwt, setJwt] = useState("");
  return (
    <SetJwtStateContext.Provider value={setJwt}>
      <JwtStateContext.Provider value={jwt}>
        {children}
      </JwtStateContext.Provider>
    </SetJwtStateContext.Provider>
  );
};

export const useJwt = () => useContext(JwtStateContext);
export const useSetJwt = () => useContext(SetJwtStateContext);
