import { useState, useEffect } from "react";

const fetchMovies = async () => {
  try {
    const res = await fetch(`http://localhost:4000/v1/genres`);
    if (!res.ok) {
      console.log(res);
      throw new Error(res.statusText);
    }
    return res.json();
  } catch (error) {
    return error.message;
  }
};
export default function LazyGenres({ moviegenres }) {
  console.log(moviegenres);
  const [state, setState] = useState({
    genres: [],
    error: null,
    loading: true,
    tagGenre:
      Object.values(moviegenres).length > 0 ? Object.values(moviegenres) : [],
  });
  useEffect(() => {
    const fetchData = async () => {
      const res = await fetchMovies();
      if (!res.genres) {
        setState((prev) => ({ ...prev, loading: false, error: res }));
      } else {
        setState((prev) => ({
          ...prev,
          loading: false,
          genres: res.genres,
        }));
      }
    };
    fetchData();
  }, []);

  const handleTag = (e) => {
    const [value, name] = e.target.value.split("-");
    let newArray = state.tagGenre;
    newArray.push(name);
    setState((prev) => ({ ...prev, tagGenre: newArray }));
  };
  const onDelete = (name) => {
    let newArray = state.tagGenre;

    newArray.filter((el) => el.toLowerCase() !== name.toLowerCase());

    setState((prev) => ({
      ...prev,
      tagGenre: newArray.filter((el) => el !== name),
    }));
  };
  return (
    <div className="level">
      <div className="level-left">
        <div className="field">
          <label className="label">Genres</label>
          <div className="control">
            <div className="select">
              <select onChange={handleTag}>
                <option value={0}>Choose...</option>
                {state.genres.map((g) => (
                  <option key={g.id} value={`${g.id}-${g.genre_name}`}>
                    {g.genre_name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>
      <div className="level-right">
        <div className="tags">
          {state.tagGenre.map((el) => (
            <span className="tag " key={el}>
              {el}
              <button
                className="delete is-small"
                onClick={() => onDelete(el)}
              ></button>
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}
