import FormInput from "./form-input";

export default function Form(props) {
  const { handlerChange, state } = props;
  const {
    title,
    description,
    year,
    release_date,
    genres,
    mpaa_rating,
    rating,
    runtime,
    errors,
  } = state;

  return (
    <>
      <FormInput
        label="title"
        name="title"
        value={title}
        handlerChange={handlerChange}
        error={errors && errors.includes(title)}
      />
      <FormInput
        label="year"
        name="year"
        value={year}
        handlerChange={handlerChange}
      />
      <FormInput
        label="runtime"
        name="runtime"
        value={runtime}
        handlerChange={handlerChange}
      />
      <FormInput
        label="rating"
        name="rating"
        value={rating}
        handlerChange={handlerChange}
      />
      <FormInput
        label="mpaa rating"
        name="mpaa_rating"
        value={mpaa_rating}
        handlerChange={handlerChange}
      />
      <FormInput
        label="release date"
        name="release_date"
        value={release_date}
        handlerChange={handlerChange}
      />

      <FormInput
        label="genres"
        name="genres"
        value={genres}
        handlerChange={handlerChange}
      />
      <FormInput
        label="description"
        name="description"
        value={description}
        handlerChange={handlerChange}
      />
    </>
  );
}
