import React, { useState, useEffect } from "react";
import Form from "../components/form";

const LazyMovies = React.lazy(() => import("../components/lazy-movies-edit"));

const initValues = {
  title: "",
  release_date: "",
  runtime: "",
  mpaa_rating: "",
  rating: "",
  description: "",
  genres: "",
  year: "",
};

const fetchMovieByID = async (id) => {
  try {
    const res = await fetch(`http://localhost:4000/v1/movie/${id}`);
    if (!res.ok) {
      console.log(res);
      throw new Error(res.statusText);
    }
    return res.json();
  } catch (error) {
    return error.message;
  }
};
export default function AddEditMovie() {
  const [state, setState] = useState(initValues);
  const [edit, setEdit] = useState(null);

  const handlerChange = (e) => {
    if (!e.target) {
      setState((prev) => ({ ...prev, release_date: e }));
      return;
    }
    const { name, value } = e.target;

    setState((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(state.genres.split(","));
    const data = new FormData(e.target);
    const payload = Object.fromEntries(data.entries());

    fetch("http://localhost:4000/v1/admin/editmovie", {
      body: JSON.stringify({ ...payload, genres: payload.genres.split(",") }),
      method: "POST",
      // headers: {
      //   "Content-Type": "application/json",
      //   // 'Content-Type': 'application/x-www-form-urlencoded',
      // },
    }).then((res) => console.log(res));
  };
  useEffect(() => {
    const fetchData = async () => {
      const res = await fetchMovieByID(edit);

      setState({
        ...res.movie,
        genres: Object.values(res.movie.genres)
          .map((val) => val)
          .toString(),
      });
    };
    if (edit > 0) {
      fetchData();
    } else {
      setState(initValues);
    }
  }, [edit]);

  return (
    <section className="hero">
      <div className="hero-body">
        <div className="level">
          <p className="title level-left">Add/Edit Movie</p>
          <div className="select level-right">
            <select onChange={(e) => setEdit(e.target.value)}>
              <option value={0}>New...</option>
              <React.Suspense fallback={<>...</>}>
                <LazyMovies />
              </React.Suspense>
            </select>
          </div>
        </div>

        <div className="content">
          <form onSubmit={handleSubmit}>
            <Form handlerChange={handlerChange} state={state} />
            <div className="field is-grouped is-grouped-right">
              <p className="control">
                <button className="button is-primary" type="submit">
                  Save
                </button>
              </p>
              <p className="control">
                <button className="button is-light">Cancel</button>
              </p>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
}
